INSERT INTO `builtin_metrics` VALUES (188, 'Categraf', 'Linux', 'CPU Guest 时间占比', 'percent', 'zh_CN', 'CPU 为 Guest OS 运行虚拟 CPU 的时间占比。\n\n- 标签 `cpu=\"cpu-total\"`，表示整机的统计值\n- 标签 `cpu=\"0\"`，表示 0 号 CPU 的统计值', 'cpu_usage_guest', unix_timestamp(now()), 'system', unix_timestamp(now()), 'system', 1717556327758798000);
INSERT INTO `builtin_metrics` VALUES (189, 'Categraf', 'Linux', 'CPU iowait 时间占比', 'percent', 'zh_CN', '表示 CPU 花费在等待 I/O 操作（输入/输出）完成的时间比例\n\n- 标签 `cpu=\"cpu-total\"`，表示整机的 iowait 占用率\n- 标签 `cpu=\"0\"`，表示 0 号 CPU 的 iowait 占用率', 'cpu_usage_iowait', unix_timestamp(now()), 'system', unix_timestamp(now()), 'system', 1717556327761258000);
INSERT INTO `builtin_metrics` VALUES (190, 'Categraf', 'Linux', 'CPU Steal 时间占比', 'percent', 'zh_CN', '表示虚拟机（Guest OS）CPU 时间被虚拟化管理器（Hypervisor）用于运行其他虚拟机的时间比例。物理机中这个指标值是 0，没有用处；虚拟机中这个指标值长时间大于 10 表示争抢严重。[参考资料](https://mp.weixin.qq.com/s/zlrfMNrDHJVE5lkTVow5zA)\n\n- 标签 `cpu=\"cpu-total\"`，表示整机的统计值\n- 标签 `cpu=\"0\"`，表示 0 号 CPU 的统计值', 'cpu_usage_steal', unix_timestamp(now()), 'system', unix_timestamp(now()), 'system', 1717556327764051000);
INSERT INTO `builtin_metrics` VALUES (191, 'Categraf', 'Linux', 'CPU 低优先级进程的时间占比', 'percent', 'zh_CN', '在Linux系统中，每个进程都有一个“nice”值，这个值的范围通常是从-20到19。默认的 nice 值为0。负的 nice 值表示高优先级（更快的 CPU 访问），而正的 nice 值表示低优先级（更慢的 CPU 访问）。该指标统计 nice 值为 1-19 的进程的时间占比。\n\n- 标签 `cpu=\"cpu-total\"`，表示整机的统计值\n- 标签 `cpu=\"0\"`，表示 0 号 CPU 的统计值', 'cpu_usage_nice', unix_timestamp(now()), 'system', unix_timestamp(now()), 'system', 1717556327766578000);
INSERT INTO `builtin_metrics` VALUES (192, 'Categraf', 'Linux', 'CPU 内核态时间占比', 'percent', 'zh_CN', '表示 CPU 内核态的时间占比\n\n- 标签 `cpu=\"cpu-total\"`，表示整机的统计值\n- 标签 `cpu=\"0\"`，表示 0 号 CPU 的统计值', 'cpu_usage_system', unix_timestamp(now()), 'system', unix_timestamp(now()), 'system', 1717556327768675000);
