INSERT INTO `builtin_metrics` VALUES (230, 'Categraf', 'Linux', '硬盘 IO - 写请求平均耗时', 'milliseconds', 'zh_CN', '', 'irate(diskio_write_time[2m])/irate(diskio_writes[2m])', unix_timestamp(now()), 'system', unix_timestamp(now()), 'system', 1717556327853833000);
INSERT INTO `builtin_metrics` VALUES (231, 'Categraf', 'Linux', '硬盘 IO - 写请求等待处理的时间', 'milliseconds', 'zh_CN', '写入请求在磁盘上等待的时间。同时等待的多个写入请求会增加该数字。例如，如果 8 个请求均平均等待 1000 毫秒，则报告 8000。', 'diskio_write_time', unix_timestamp(now()), 'system', unix_timestamp(now()), 'system', 1717556327856133000);
INSERT INTO `builtin_metrics` VALUES (232, 'Categraf', 'Linux', '硬盘 IO - 加权 IO 处理时间', 'milliseconds', 'zh_CN', '只要设备在处理 IO，不管同时处理了几个，diskio_io_time 就会递增；同时处理的 IO 数量乘以时间就得到加权的值：diskio_weighted_io_time。', 'diskio_weighted_io_time', unix_timestamp(now()), 'system', unix_timestamp(now()), 'system', 1717556327858277000);
INSERT INTO `builtin_metrics` VALUES (233, 'Categraf', 'Linux', '硬盘 IO - 时间维度 Utilization', 'percent', 'zh_CN', '在时间维度统计硬盘 IO 时间占比，比如该值是 50%，表示有 50% 的时间是在处理 IO，该值 100%，表示一直在处理 IO，但是注意，现代磁盘设备具备并行处理多个 I/O 请求的能力，所以即便该值是 100%，可能硬盘还是可以接收新的处理请求。\n\n比如某人有两只手，最近 1 分钟一直在用单手劳动，从时间维度来看，利用率是 100%，但即便是 100%，再给他更多的活，他也能干，因为他还有一只手可用。', 'irate(diskio_io_time[2m])/1000*100', unix_timestamp(now()), 'system', unix_timestamp(now()), 'system', 1717556327860467000);
INSERT INTO `builtin_metrics` VALUES (234, 'Categraf', 'Linux', '硬盘 IO - 每秒写入字节数量', 'bytesSecIEC', 'zh_CN', '', 'irate(diskio_write_bytes[2m])', unix_timestamp(now()), 'system', unix_timestamp(now()), 'system', 1717556327862401000);
INSERT INTO `builtin_metrics` VALUES (235, 'Categraf', 'Linux', '硬盘 IO - 每秒写次数', 'none', 'zh_CN', '', 'irate(diskio_writes{}[2m])', unix_timestamp(now()), 'system', unix_timestamp(now()), 'system', 1717556327864213000);
INSERT INTO `builtin_metrics` VALUES (236, 'Categraf', 'Linux', '硬盘 IO - 每秒合并写请求次数', 'none', 'zh_CN', '相邻的读取和写入可能会为了效率而被合并。因此，在最终交给磁盘之前，两个4K的读取可能会变成一个8K的读取，因此它将被计数（和排队）为仅有一个I/O。这些字段让你知道这种情况发生的频率。', 'irate(diskio_merged_writes[2m])', unix_timestamp(now()), 'system', unix_timestamp(now()), 'system', 1717556327866432000);
INSERT INTO `builtin_metrics` VALUES (237, 'Categraf', 'Linux', '硬盘 IO - 每秒合并读请求次数', 'none', 'zh_CN', '相邻的读取和写入可能会为了效率而被合并。因此，在最终交给磁盘之前，两个4K的读取可能会变成一个8K的读取，因此它将被计数（和排队）为仅有一个I/O。这些字段让你知道这种情况发生的频率。', 'irate(diskio_merged_reads[2m])', unix_timestamp(now()), 'system', unix_timestamp(now()), 'system', 1717556327868237000);
INSERT INTO `builtin_metrics` VALUES (238, 'Categraf', 'Linux', '硬盘 IO - 每秒读取字节数量', 'bytesSecIEC', 'zh_CN', '', 'irate(diskio_read_bytes[2m])', unix_timestamp(now()), 'system', unix_timestamp(now()), 'system', 1717556327870357000);
