INSERT INTO `builtin_metrics` VALUES (240, 'Categraf', 'Linux', '硬盘 IO - 硬盘在处理 IO 的时间', 'milliseconds', 'zh_CN', '硬盘在处理 IO 的时间，单位是毫秒。假设最近 1 秒内有 500 毫秒是在处理 IO，这个值就要加 500，这是一个单调递增的 Counter 值，从操作系统启动之后就会一直涨。实际使用的时候，一般使用该指标计算硬盘 IO UTIL（时间维度的百分比）。', 'diskio_io_time', unix_timestamp(now()), 'system', unix_timestamp(now()), 'system', 1717556327874220000);
INSERT INTO `builtin_metrics` VALUES (241, 'Categraf', 'Linux', '硬盘 IO - 设备驱动程序正在处理的 IO 数量', 'none', 'zh_CN', '当前已经分配给设备驱动程序并且尚未完成的请求数量，Gauge 类型。如果 I/O 请求已经进入队列，但是尚未分配给设备驱动程序，则不统计在内。', 'diskio_iops_in_progress', unix_timestamp(now()), 'system', unix_timestamp(now()), 'system', 1717556327876524000);
