INSERT INTO `builtin_metrics` VALUES (243, 'Categraf', 'Linux', '硬盘 IO - 读请求等待处理的时间', 'milliseconds', 'zh_CN', '读取请求在磁盘上等待的时间。同时等待的多个读取请求会增加该数字。例如，如果 5 个请求均平均等待 100 毫秒，则报告 500。', 'diskio_read_time', unix_timestamp(now()), 'system', unix_timestamp(now()), 'system', 1717556327880512000);
INSERT INTO `builtin_metrics` VALUES (244, 'Categraf', 'Linux', '硬盘 IO - 队列深度', 'none', 'zh_CN', '假设过去 1000 毫秒内有 5000 毫秒的加权等待时间，相当于平均有 5 个 IO 请求在等待，即队列深度为 5。', 'irate(diskio_weighted_io_time[1m])/1000', unix_timestamp(now()), 'system', unix_timestamp(now()), 'system', 1717556327882423000);
