INSERT INTO `builtin_payloads` VALUES (390, 1717556328087990000, 'dashboard', 'MySQL', '', 'MySQL Overview by categraf, group by ident', '', '{\"id\":0,\"group_id\":0,\"name\":\"MySQL Overview by categraf, group by ident\",\"ident\":\"\",\"tags\":\"\",\"create_at\":0,\"create_by\":\"\",\"update_at\":0,\"update_by\":\"\",\"configs\":{\"panels\":[{\"collapsed\":true,\"id\":\"dfd77e6d-4e88-4bd9-8c19-74f566920f6c\",\"layout\":{\"h\":1,\"i\":\"dfd77e6d-4e88-4bd9-8c19-74f566920f6c\",\"isResizable\":false,\"w\":24,\"x\":0,\"y\":0},\"name\":\"Basic Info\",\"panels\":[],\"type\":\"row\"},{\"custom\":{\"calc\":\"lastNotNull\",\"colSpan\":1,\"colorMode\":\"value\",\"textMode\":\"valueAndName\",\"textSize\":{\"title\":12,\"value\":24}},\"datasourceCate\":\"prometheus\",\"datasourceValue\":\"${datasource}\",\"id\":\"74a5cd8c-f870-442d-bda6-48b5ce4e87ea\",\"layout\":{\"h\":3,\"i\":\"74a5cd8c-f870-442d-bda6-48b5ce4e87ea\",\"isResizable\":true,\"w\":12,\"x\":0,\"y\":1},\"name\":\"MySQL Uptime Days\",\"options\":{\"standardOptions\":{\"decimals\":1,\"util\":\"none\"},\"valueMappings\":[{\"match\":{\"to\":1800},\"result\":{\"color\":\"#ec7718\"},\"type\":\"range\"},{\"match\":{\"from\":1800},\"result\":{\"color\":\"#369603\"},\"type\":\"range\"}]},\"targets\":[{\"expr\":\"mysql_global_status_uptime{ident=~\\\"$ident\\\"}/3600/24\",\"legend\":\"{{ident}} {{address}}\"}],\"type\":\"stat\",\"version\":\"2.0.0\"},{\"custom\":{\"calc\":\"lastNotNull\",\"colSpan\":1,\"colorMode\":\"value\",\"textMode\":\"valueAndName\",\"textSize\":{\"title\":12,\"value\":24}},\"datasourceCate\":\"prometheus\",\"datasourceValue\":\"${datasource}\",\"description\":\"mysql_global_status_queries\",\"id\":\"1763bcc6-d058-4a2b-a099-3d590debd01a\",\"layout\":{\"h\":3,\"i\":\"1763bcc6-d058-4a2b-a099-3d590debd01a\",\"isResizable\":true,\"w\":12,\"x\":12,\"y\":1},\"name\":\"Current QPS\",\"options\":{\"standardOptions\":{\"decimals\":2},\"valueMappings\":[{\"match\":{\"to\":100},\"result\":{\"color\":\"#05a31f\"},\"type\":\"range\"},{\"match\":{\"from\":100},\"result\":{\"color\":\"#ea3939\"},\"type\":\"range\"}]},\"targets\":[{\"expr\":\"rate(mysql_global_status_queries{ident=~\\\"$ident\\\"}[5m])\",\"legend\":\"{{ident}} {{address}}\"}],\"type\":\"stat\",\"version\":\"2.0.0\"},{\"custom\":{\"calc\":\"lastNotNull\",\"colSpan\":1,\"colorMode\":\"value\",\"textMode\":\"valueAndName\",\"textSize\":{\"title\":12,\"value\":24}},\"datasourceCate\":\"prometheus\",\"datasourceValue\":\"${datasource}\",\"description\":\"**InnoDB Buffer Pool Size**\\n\\nInnoDB maintains a storage area called the buffer pool for caching data and indexes in memory.  Knowing how the InnoDB buffer pool works, and taking advantage of it to keep frequently accessed data in memory, is one of the most important aspects of MySQL tuning. The goal is to keep the working set in memory. In most cases, this should be between 60%-90% of available memory on a dedicated database host, but depends on many factors.\",\"id\":\"28d16171-9e36-4f5d-87be-95bcb2aeb643\",\"layout\":{\"h\":3,\"i\":\"28d16171-9e36-4f5d-87be-95bcb2aeb643\",\"isResizable\":true,\"w\":12,\"x\":0,\"y\":4},\"name\":\"InnoDB Buffer Pool\",\"options\":{\"standardOptions\":{\"util\":\"bytesIEC\"}},\"targets\":[{\"expr\":\"mysql_global_variables_innodb_buffer_pool_size{ident=~\\\"$ident\\\"}\",\"legend\":\"{{ident}} {{address}}\"}],\"type\":\"stat\",\"version\":\"2.0.0\"},{\"custom\":{\"calc\":\"lastNotNull\",\"colSpan\":1,\"colorMode\":\"value\",\"textMode\":\"valueAndName\",\"textSize\":{\"title\":12,\"value\":24}},\"datasourceCate\":\"prometheus\",\"datasourceValue\":\"${datasource}\",\"description\":\"**Table Locks**\\n\\nMySQL takes a number of different locks for varying reasons. In this graph we see how many Table level locks MySQL has requested from the storage engine. In the case of InnoDB, many times the locks could actually be row locks as it only takes table level locks in a few specific cases.\\n\\nIt is most useful to compare Locks Immediate and Locks Waited. If Locks waited is rising, it means you have lock contention. Otherwise, Locks Immediate rising and falling is normal activity.\",\"id\":\"5fe39015-bf33-4f02-b79e-a8977e56d7ca\",\"layout\":{\"h\":3,\"i\":\"5fe39015-bf33-4f02-b79e-a8977e56d7ca\",\"isResizable\":true,\"w\":6,\"x\":12,\"y\":4},\"name\":\"Table Locks Waited(5min)\",\"options\":{\"standardOptions\":{},\"valueMappings\":[{\"match\":{\"from\":1},\"result\":{\"color\":\"#e70d0d\"},\"type\":\"range\"},{\"match\":{\"to\":1},\"result\":{\"color\":\"#53b503\"},\"type\":\"range\"}]},\"targets\":[{\"expr\":\"increase(mysql_global_status_table_locks_waited{ident=~\\\"$ident\\\"}[5m])\",\"legend\":\"{{ident}} {{address}}\"}],\"type\":\"stat\",\"version\":\"2.0.0\"},{\"custom\":{\"drawStyle\":\"lines\",\"fillOpacity\":0.3,\"gradientMode\":\"opacity\",\"lineInterpolation\":\"smooth\",\"lineWidth\":2,\"stack\":\"off\"},\"datasourceCate\":\"prometheus\",\"datasourceValue\":\"${datasource}\",\"id\":\"70ee692b-24d9-4807-81b4-81582b5526c2\",\"layout\":{\"h\":3,\"i\":\"70ee692b-24d9-4807-81b4-81582b5526c2\",\"isResizable\":true,\"w\":6,\"x\":18,\"y\":4},\"name\":\"Slave Replication Lag\",\"options\":{\"legend\":{\"displayMode\":\"hidden\"},\"standardOptions\":{},\"thresholds\":{},\"tooltip\":{\"mode\":\"all\",\"sort\":\"desc\"}},\"targets\":[{\"expr\":\"mysql_slave_status_seconds_behind_master{ident=~\\\"$ident\\\"} - mysql_slave_status_sql_delay{ident=~\\\"$ident\\\"}\",\"legend\":\"{{ident}} {{address}}\",\"refId\":\"A\"}],\"type\":\"timeseries\",\"version\":\"2.0.0\"},{\"collapsed\":true,\"id\":\"c6da1a55-04d2-4e3e-a22f-e5790182da4a\",\"layout\":{\"h\":1,\"i\":\"c6da1a55-04d2-4e3e-a22f-e5790182da4a\",\"isResizable\":false,\"w\":24,\"x\":0,\"y\":7},\"name\":\"Connections\",\"panels\":[],\"type\":\"row\"},{\"custom\":{\"drawStyle\":\"lines\",\"fillOpacity\":0.05,\"lineInterpolation\":\"smooth\",\"stack\":\"off\"},\"datasourceCate\":\"prometheus\",\"datasourceValue\":\"${datasource}\",\"description\":\"**Max Connections** \\n\\nMax Connections is the maximum permitted number of simultaneous client connections. By default, this is 151. Increasing this value increases the number of file descriptors that mysqld requires. If the required number of descriptors are not available, the server reduces the value of Max Connections.\\n\\nmysqld actually permits Max Connections + 1 clients to connect. The extra connection is reserved for use by accounts that have the SUPER privilege, such as root.\\n\\nMax Used Connections is the maximum number of connections that have been in use simultaneously since the server started.\\n\\nConnections is the number of connection attempts (successful or not) to the MySQL server.\",\"id\":\"458753cc-a6d0-4afc-bf5e-54585dc5990c\",\"layout\":{\"h\":5,\"i\":\"458753cc-a6d0-4afc-bf5e-54585dc5990c\",\"isResizable\":true,\"w\":6,\"x\":0,\"y\":8},\"name\":\"MySQL Connections\",\"options\":{\"legend\":{\"displayMode\":\"hidden\"},\"standardOptions\":{},\"thresholds\":{},\"tooltip\":{\"mode\":\"all\",\"sort\":\"desc\"}},\"targets\":[{\"expr\":\"mysql_global_status_threads_connected{ident=~\\\"$ident\\\"}\",\"legend\":\"{{ident}} {{address}} Connections\"}],\"type\":\"timeseries\",\"version\":\"2.0.0\"},{\"custom\":{\"drawStyle\":\"lines\",\"fillOpacity\":0.05,\"gradientMode\":\"none\",\"lineInterpolation\":\"smooth\",\"lineWidth\":1,\"scaleDistribution\":{\"type\":\"linear\"},\"spanNulls\":false,\"stack\":\"off\"},\"datasourceCate\":\"prometheus\",\"datasourceValue\":\"${datasource}\",\"description\":\"**Max Connections** \\n\\nMax Connections is the maximum permitted number of simultaneous client connections. By default, this is 151. Increasing this value increases the number of file descriptors that mysqld requires. If the required number of descriptors are not available, the server reduces the value of Max Connections.\\n\\nmysqld actually permits Max Connections + 1 clients to connect. The extra connection is reserved for use by accounts that have the SUPER privilege, such as root.\\n\\nMax Used Connections is the maximum number of connections that have been in use simultaneously since the server started.\\n\\nConnections is the number of connection attempts (successful or not) to the MySQL server.\",\"id\":\"ebf01aad-c07b-4541-9891-bb3d5a7175a6\",\"layout\":{\"h\":5,\"i\":\"13bf0230-db47-4338-9b32-8e15af8915e4\",\"isResizable\":true,\"w\":6,\"x\":6,\"y\":8},\"name\":\"MySQL Connections Used Percent\",\"options\":{\"legend\":{\"displayMode\":\"hidden\"},\"standardOptions\":{\"util\":\"percentUnit\"},\"thresholds\":{\"steps\":[{\"color\":\"#634CD9\",\"type\":\"base\",\"value\":null}]},\"tooltip\":{\"mode\":\"all\",\"sort\":\"desc\"}},\"targets\":[{\"expr\":\"mysql_global_status_threads_connected{ident=~\\\"$ident\\\"}/mysql_global_variables_max_connections{ident=~\\\"$ident\\\"}\",\"legend\":\"{{ident}} {{address}}\"}],\"type\":\"timeseries\",\"version\":\"2.0.0\"},{\"custom\":{\"drawStyle\":\"lines\",\"fillOpacity\":0.3,\"gradientMode\":\"opacity\",\"lineInterpolation\":\"smooth\",\"lineWidth\":2,\"stack\":\"off\"},\"datasourceCate\":\"prometheus\",\"datasourceValue\":\"${datasource}\",\"description\":\"Threads Connected is the number of open connections, while Threads Running is the number of threads not sleeping.\",\"id\":\"f18e13bf-5495-492f-95c5-4a590e38c58e\",\"layout\":{\"h\":5,\"i\":\"f18e13bf-5495-492f-95c5-4a590e38c58e\",\"isResizable\":true,\"w\":6,\"x\":12,\"y\":8},\"name\":\"MySQL Client Thread Running\",\"options\":{\"legend\":{\"displayMode\":\"hidden\"},\"standardOptions\":{},\"thresholds\":{},\"tooltip\":{\"mode\":\"all\",\"sort\":\"desc\"}},\"targets\":[{\"expr\":\"mysql_global_status_threads_running{ident=~\\\"$ident\\\"}\",\"legend\":\"{{ident}} {{address}} Threads Running\"}],\"type\":\"timeseries\",\"version\":\"2.0.0\"},{\"custom\":{\"drawStyle\":\"lines\",\"fillOpacity\":0.05,\"lineInterpolation\":\"smooth\",\"stack\":\"off\"},\"datasourceCate\":\"prometheus\",\"datasourceValue\":\"${datasource}\",\"description\":\"**Max Connections** \\n\\nMax Connections is the maximum permitted number of simultaneous client connections. By default, this is 151. Increasing this value increases the number of file descriptors that mysqld requires. If the required number of descriptors are not available, the server reduces the value of Max Connections.\\n\\nmysqld actually permits Max Connections + 1 clients to connect. The extra connection is reserved for use by accounts that have the SUPER privilege, such as root.\\n\\nMax Used Connections is the maximum number of connections that have been in use simultaneously since the server started.\\n\\nConnections is the number of connection attempts (successful or not) to the MySQL server.\",\"id\":\"86251111-3a14-4c52-b1f2-a5cbe009bc0f\",\"layout\":{\"h\":5,\"i\":\"34bd296e-bea3-4638-9a35-f97121e804b2\",\"isResizable\":true,\"w\":6,\"x\":18,\"y\":8},\"name\":\"Max Used and Aborted Connections\",\"options\":{\"legend\":{\"displayMode\":\"hidden\"},\"standardOptions\":{},\"thresholds\":{},\"tooltip\":{\"mode\":\"all\",\"sort\":\"desc\"}},\"targets\":[{\"expr\":\"mysql_global_status_max_used_connections{ident=~\\\"$ident\\\"}\",\"legend\":\"{{ident}} {{address}} Max Used Connections\"},{\"expr\":\"rate(mysql_global_status_aborted_connects{ident=~\\\"$ident\\\"}[5m])\",\"legend\":\"{{ident}} {{address}} Aborted Connections\"}],\"type\":\"timeseries\",\"version\":\"2.0.0\"},{\"collapsed\":true,\"id\":\"462559f7-06d3-4585-9ad3-a0906e7c362d\",\"layout\":{\"h\":1,\"i\":\"462559f7-06d3-4585-9ad3-a0906e7c362d\",\"isResizable\":false,\"w\":24,\"x\":0,\"y\":13},\"name\":\"Query Performance\",\"panels\":[],\"type\":\"row\"},{\"custom\":{\"drawStyle\":\"lines\",\"fillOpacity\":0.3,\"gradientMode\":\"opacity\",\"lineInterpolation\":\"smooth\",\"lineWidth\":2,\"stack\":\"off\"},\"datasourceCate\":\"prometheus\",\"datasourceValue\":\"${datasource}\",\"id\":\"0428fde5-3fbf-45dd-b1a9-1a498d6c2de4\",\"layout\":{\"h\":4,\"i\":\"0428fde5-3fbf-45dd-b1a9-1a498d6c2de4\",\"isResizable\":true,\"w\":12,\"x\":0,\"y\":14},\"name\":\"MySQL Temporary Objects\",\"options\":{\"legend\":{\"displayMode\":\"hidden\"},\"standardOptions\":{},\"thresholds\":{},\"tooltip\":{\"mode\":\"all\",\"sort\":\"desc\"}},\"targets\":[{\"expr\":\"rate(mysql_global_status_created_tmp_tables{ident=~\\\"$ident\\\"}[5m])\",\"legend\":\"{{ident}} {{address}} Created Tmp Tables\"},{\"expr\":\"rate(mysql_global_status_created_tmp_disk_tables{ident=~\\\"$ident\\\"}[5m])\",\"legend\":\"{{ident}} {{address}} Created Tmp Disk Tables\"},{\"expr\":\"rate(mysql_global_status_created_tmp_files{ident=~\\\"$ident\\\"}[5m])\",\"legend\":\"{{ident}} {{address}} Created Tmp Files\"}],\"type\":\"timeseries\",\"version\":\"2.0.0\"},{\"custom\":{\"drawStyle\":\"lines\",\"fillOpacity\":0.3,\"gradientMode\":\"opacity\",\"lineInterpolation\":\"smooth\",\"lineWidth\":2,\"stack\":\"off\"},\"datasourceCate\":\"prometheus\",\"datasourceValue\":\"${datasource}\",\"description\":\"**MySQL Select Types**\\n\\nAs with most relational databases, selecting based on indexes is more efficient than scanning an entire table\'s data. Here we see the counters for selects not done with indexes.\\n\\n* ***Select Scan*** is how many queries caused full table scans, in which all the data in the table had to be read and either discarded or returned.\\n* ***Select Range*** is how many queries used a range scan, which means MySQL scanned all rows in a given range.\\n* ***Select Full Join*** is the number of joins that are not joined on an index, this is usually a huge performance hit.\",\"id\":\"7333267f-e76e-495a-b3d8-08b100ab1330\",\"layout\":{\"h\":4,\"i\":\"7333267f-e76e-495a-b3d8-08b100ab1330\",\"isResizable\":true,\"w\":12,\"x\":12,\"y\":14},\"name\":\"MySQL Select Types\",\"options\":{\"legend\":{\"displayMode\":\"hidden\"},\"standardOptions\":{},\"thresholds\":{},\"tooltip\":{\"mode\":\"all\",\"sort\":\"desc\"}},\"targets\":[{\"expr\":\"rate(mysql_global_status_select_ful