INSERT INTO `notify_tpl` VALUES (1, 'email', 'email', '<!DOCTYPE html>\n	<html lang=\"en\">\n	<head>\n		<meta charset=\"UTF-8\">\n		<meta http-equiv=\"X-UA-Compatible\" content=\"ie=edge\">\n		<title>夜莺告警通知</title>\n		<style type=\"text/css\">\n			.wrapper {\n				background-color: #f8f8f8;\n				padding: 15px;\n				height: 100%;\n			}\n			.main {\n				width: 600px;\n				padding: 30px;\n				margin: 0 auto;\n				background-color: #fff;\n				font-size: 12px;\n				font-family: verdana,\'Microsoft YaHei\',Consolas,\'Deja Vu Sans Mono\',\'Bitstream Vera Sans Mono\';\n			}\n			header {\n				border-radius: 2px 2px 0 0;\n			}\n			header .title {\n				font-size: 14px;\n				color: #333333;\n				margin: 0;\n			}\n			header .sub-desc {\n				color: #333;\n				font-size: 14px;\n				margin-top: 6px;\n				margin-bottom: 0;\n			}\n			hr {\n				margin: 20px 0;\n				height: 0;\n				border: none;\n				border-top: 1px solid #e5e5e5;\n			}\n			em {\n				font-weight: 600;\n			}\n			table {\n				margin: 20px 0;\n				width: 100%;\n			}\n	\n			table tbody tr{\n				font-weight: 200;\n				font-size: 12px;\n				color: #666;\n				height: 32px;\n			}\n	\n			.succ {\n				background-color: green;\n				color: #fff;\n			}\n	\n			.fail {\n				background-color: red;\n				color: #fff;\n			}\n	\n			.succ th, .succ td, .fail th, .fail td {\n				color: #fff;\n			}\n	\n			table tbody tr th {\n				width: 80px;\n				text-align: right;\n			}\n			.text-right {\n				text-align: right;\n			}\n			.body {\n				margin-top: 24px;\n			}\n			.body-text {\n				color: #666666;\n				-webkit-font-smoothing: antialiased;\n			}\n			.body-extra {\n				-webkit-font-smoothing: antialiased;\n			}\n			.body-extra.text-right a {\n				text-decoration: none;\n				color: #333;\n			}\n			.body-extra.text-right a:hover {\n				color: #666;\n			}\n			.button {\n				width: 200px;\n				height: 50px;\n				margin-top: 20px;\n				text-align: center;\n				border-radius: 2px;\n				background: #2D77EE;\n				line-height: 50px;\n				font-size: 20px;\n				color: #FFFFFF;\n				cursor: pointer;\n			}\n			.button:hover {\n				background: rgb(25, 115, 255);\n				border-color: rgb(25, 115, 255);\n				color: #fff;\n			}\n			footer {\n				margin-top: 10px;\n				text-align: right;\n			}\n			.footer-logo {\n				text-align: right;\n			}\n			.footer-logo-image {\n				width: 108px;\n				height: 27px;\n				margin-right: 10px;\n			}\n			.copyright {\n				margin-top: 10px;\n				font-size: 12px;\n				text-align: right;\n				color: #999;\n				-webkit-font-smoothing: antialiased;\n			}\n		</style>\n	</head>\n	<body>\n	<div class=\"wrapper\">\n		<div class=\"main\">\n			<header>\n				<h3 class=\"title\">{{.RuleName}}</h3>\n				<p class=\"sub-desc\"></p>\n			</header>\n	\n			<hr>\n	\n			<div class=\"body\">\n				<table cellspacing=\"0\" cellpadding=\"0\" border=\"0\">\n					<tbody>\n					{{if .IsRecovered}}\n					<tr class=\"succ\">\n						<th>级别状态：</th>\n						<td>S{{.Severity}} Recovered</td>\n					</tr>\n					{{else}}\n					<tr class=\"fail\">\n						<th>级别状态：</th>\n						<td>S{{.Severity}} Triggered</td>\n					</tr>\n					{{end}}\n	\n					<tr>\n						<th>策略备注：</th>\n						<td>{{.RuleNote}}</td>\n					</tr>\n					<tr>\n						<th>设备备注：</th>\n						<td>{{.TargetNote}}</td>\n					</tr>\n					{{if not .IsRecovered}}\n					<tr>\n						<th>触发时值：</th>\n						<td>{{.TriggerValue}}</td>\n					</tr>\n					{{end}}\n	\n					{{if .TargetIdent}}\n					<tr>\n						<th>监控对象：</th>\n						<td>{{.TargetIdent}}</td>\n					</tr>\n					{{end}}\n					<tr>\n						<th>监控指标：</th>\n						<td>{{.TagsJSON}}</td>\n					</tr>\n	\n					{{if .IsRecovered}}\n					<tr>\n						<th>恢复时间：</th>\n						<td>{{timeformat .LastEvalTime}}</td>\n					</tr>\n					{{else}}\n					<tr>\n						<th>触发时间：</th>\n						<td>\n							{{timeformat .TriggerTime}}\n						</td>\n					</tr>\n					{{end}}\n	\n					<tr>\n						<th>发送时间：</th>\n						<td>\n							{{timestamp}}\n						</td>\n					</tr>\n					</tbody>\n				</table>\n	\n				<hr>\n	\n				<footer>\n					<div class=\"copyright\" style=\"font-style: italic\">\n						报警太多？使用 <a href=\"https://flashcat.cloud/product/flashduty/\" target=\"_blank\">FlashDuty</a> 做告警聚合降噪、排班OnCall！\n					</div>\n				</footer>\n			</div>\n		</div>\n	</div>\n	</body>\n	</html>', 0, '', 0, '');
INSERT INTO `notify_tpl` VALUES (2, 'feishu', 'feishu', '级别状态: S{{.Severity}} {{if .IsRecovered}}Recovered{{else}}Triggered{{end}}   \n规则名称: {{.RuleName}}{{if .RuleNote}}   \n规则备注: {{.RuleNote}}{{end}}   \n监控指标: {{.TagsJSON}}\n{{if .IsRecovered}}恢复时间：{{timeformat .LastEvalTime}}{{else}}触发时间: {{timeformat .TriggerTime}}\n触发时值: {{.TriggerValue}}{{end}}\n发送时间: {{timestamp}}', 0, '', 0, '');
INSERT INTO `notify_tpl` VALUES (3, 'feishucard', 'feishucard', '{{ if .IsRecovered }}\n{{- if ne .Cate \"host\"}}\n**告警集群:** {{.Cluster}}{{end}}   \n**级别状态:** S{{.Severity}} Recovered   \n**告警名称:** {{.RuleName}}   \n**恢复时间:** {{timeformat .LastEvalTime}}   \n**告警描述:** **服务已恢复**   \n{{- else }}\n{{- if ne .Cate \"host\"}}   \n**告警集群:** {{.Cluster}}{{end}}   \n**级别状态:** S{{.Severity}} Triggered   \n**告警名称:** {{.RuleName}}   \n**触发时间:** {{timeformat .TriggerTime}}   \n**发送时间:** {{timestamp}}   \n**触发时值:** {{.TriggerValue}}   \n{{if .RuleNote }}**告警描述:** **{{.RuleNote}}**{{end}}   \n{{- end -}}', 0, '', 0, '');
